pragma solidity >=0.6.10;
import {SafeMath} from "./../contracts/external/SafeMath.sol";

contract Betting {
    string public constant name = "ERC20Basic";
    string public constant symbol = "BSC";
    uint8 public constant decimals = 18;  

    using SafeMath for uint256;
    event Transfer(address indexed from, address indexed to, uint tokens);

    struct Bet {
        address owner;
        uint betId;
        uint256 betAmount;
        uint256 numberOfPlayers;
        uint unlockDate;
    }
    // Address of the player and => the balance 
    mapping(address => uint256) balances;
    mapping(uint => Bet) public bet;
    constructor() public {
	    balances[address(this)] = 0;
    }

    function createNewGame(uint _betAmount, uint _lockedUntil) public payable returns (uint) {
        uint rand = uint(keccak256(abi.encodePacked(block.difficulty, block.timestamp)));
        uint betId = uint(rand % (10 ** 20));
        // while not betId
        bet[betId].betAmount = _betAmount;
        bet[betId].owner = msg.sender;
        bet[betId].betId = betId;
        bet[betId].unlockDate = _lockedUntil;
        bet[betId].numberOfPlayers = 0;
        acceptInvite(betId); 
        return (betId);
    }

    function balanceOf(address tokenOwner) public view returns (uint) {
        return balances[tokenOwner];
    }

    function withdrawal(address  winner, uint256 winAmount) public payable returns (bool) {
        require(winAmount <= balanceOf(address(this)), "Insufficient funds");

        balances[address(this)] = balances[address(this)].sub(winAmount);
        payable(winner).transfer(winAmount);
        emit Transfer(winner, address(this), winAmount);

        return true;
    }

    function transferToContract(uint betId) public payable returns (bool) {
        payable(address(this)).transfer(msg.value);
        emit Transfer(msg.sender, address(this), bet[betId].betAmount);
        return true;
    }

    function acceptInvite(uint betId) public payable {
        require(bet[betId].betAmount <= msg.value, "Insufficient funds");
        bet[betId].numberOfPlayers = bet[betId].numberOfPlayers.add(1);
        balances[address(this)] = balances[address(this)].add(bet[betId].betAmount);
    }


    function payOut(address[] memory winners, uint betId) public payable {
        require(block.timestamp >= bet[betId].unlockDate, "Funds are locked");
        require(bet[betId].owner == msg.sender, "Owner needs to enable payment");

        uint256 totalAmount = bet[betId].betAmount.mul(bet[betId].numberOfPlayers);
        uint256 winningAmountPerPlayer = totalAmount/winners.length;
        for(uint256 i = 0; i < winners.length; i++){
            withdrawal(winners[i], winningAmountPerPlayer);
        }
    }

}
